  render: () => <div>
      <h3 className="text-heading-3 mb-6 font-serif text-foreground">
        Brand Colors
      </h3>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-3xl">
        <ColorSwatch name="Tomato (Primary)" hex="#e4410a" variable="--color-rostoc-orange" />
        <ColorSwatch name="Coal (Background)" hex="#222222" variable="--color-rostoc-dark" />
      </div>
      <div className="mt-8 p-6 bg-card border border-border rounded-sm">
        <h4 className="text-body font-semibold mb-3">Usage Guidelines</h4>
        <ul className="space-y-2 text-body-sm text-muted-foreground">
          <li>
            • <strong>Tomato</strong>: Primary actions, highlights, interactive
            elements
          </li>
          <li>
            • <strong>Coal</strong>: Primary background color in dark mode
          </li>
        </ul>
      </div>
    </div>
