  render: () => <div>
      <h3 className="text-heading-3 mb-6 font-serif text-foreground">
        Semantic Colors
      </h3>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-4xl">
        <ColorSwatch name="Primary" hex="#e4570a" variable="--color-primary" />
        <ColorSwatch name="Secondary" hex="#3a3a3a" variable="--color-secondary" />
        <ColorSwatch name="Accent" hex="#e4570a" variable="--color-accent" />
        <ColorSwatch name="Destructive" hex="#dc2626" variable="--color-destructive" />
        <ColorSwatch name="Muted" hex="#2a2a2a" variable="--color-muted" />
        <ColorSwatch name="Border" hex="#f4f3dd" variable="--color-border" />
      </div>
      <div className="mt-8 p-6 bg-card border border-border rounded-sm">
        <h4 className="text-body font-semibold mb-3">About Semantic Colors</h4>
        <p className="text-body-sm text-muted-foreground">
          Semantic colors are defined as CSS custom properties that adapt to the
          current theme. They provide consistent naming for common UI patterns
          across light and dark modes.
        </p>
      </div>
    </div>
