  render: () => <TokenGrid>
      <TokenSection title="Font Families">
        <div className="space-y-4">
          <div className="text-body-sm">
            <div className="font-semibold">font/family/title</div>
            <div className="text-muted-foreground font-serif text-xl">
              Space Grotesk
            </div>
          </div>
          <div className="text-body-sm">
            <div className="font-semibold">font/family/body</div>
            <div className="text-muted-foreground font-sans text-xl">
              Instrument Sans
            </div>
          </div>
        </div>
      </TokenSection>

      <TokenSection title="Font Sizes">
        <div className="space-y-4">
          <div>
            <div className="text-body-sm font-semibold mb-1">
              font/size/XL (28px)
            </div>
            <div className="text-[28px] leading-[28px]">
              The quick brown fox
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-1">
              font/size/M (20px)
            </div>
            <div className="text-[20px] leading-[27px]">
              The quick brown fox
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-1">
              font/size/S (18px)
            </div>
            <div className="text-[18px] leading-[18px]">
              The quick brown fox
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-1">
              font/size/xs (13px)
            </div>
            <div className="text-[13px] leading-[16px]">
              The quick brown fox
            </div>
          </div>
        </div>
      </TokenSection>

      <TokenSection title="Typography Styles">
        <div className="space-y-6">
          <div>
            <div className="text-body-sm font-semibold mb-2 text-muted-foreground">
              Title/Small - Space Grotesk Medium 20/27
            </div>
            <div className="font-serif font-medium text-[20px] leading-[27px]">
              The quick brown fox jumps over the lazy dog
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-2 text-muted-foreground">
              Title/XSmall - Space Grotesk Medium 13/16 UPPERCASE
            </div>
            <div className="font-serif font-medium text-[13px] leading-[16px] uppercase">
              The quick brown fox jumps over the lazy dog
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-2 text-muted-foreground">
              Body/XLarge - Instrument Sans Regular 28/28
            </div>
            <div className="font-sans font-normal text-[28px] leading-[28px]">
              The quick brown fox jumps over the lazy dog
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-2 text-muted-foreground">
              Body/Large - Instrument Sans Regular 18/18
            </div>
            <div className="font-sans font-normal text-[18px] leading-[18px]">
              The quick brown fox jumps over the lazy dog
            </div>
          </div>
          <div>
            <div className="text-body-sm font-semibold mb-2 text-muted-foreground">
              Body/Medium (caption) - Instrument Sans Regular 13/16
            </div>
            <div className="font-sans font-normal text-[13px] leading-[16px]">
              The quick brown fox jumps over the lazy dog
            </div>
          </div>
        </div>
      </TokenSection>
    </TokenGrid>
